
public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int mark = 40;
		
		while (mark == 50) {
			
			System.out.println("The mark is equal to 50");
			
		}  if (mark < 50) {

			System.out.println("The mark is less than  50");
			

		} else if (mark == 80) {
			
			System.out.println("The marks is not equal to 80");
			
		}else{
			
			System.out.println("The marks is not equal to 20");

		}

	}
}

